    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        if(user.getText().equals("") || nom.getText().equals("") || prenom.getText().equals("") || mdp.getPassword().equals("")){
            ButtonInfo bi = new ButtonInfo(new JFrame(), true);
            bi.pan.setBackground(Color.red);
            bi.titre1.setText("Erreur de champs vides");
            bi.titre2.setText("Tous les champs doivent √™tre remplis");
            bi.titre3.setText("");
            bi.setVisible(true);
        }else{
            char[] c = mdp.getPassword();
            String mdpV = new String(c);
            AdminDAO ad = new AdminDAO(prenom.getText(),nom.getText(),user.getText(),mdpV);
            ad.ajouterUser();
            user.setText("");
            mdp.setText("");
            nom.setText("");
            prenom.setText("");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

